package week1.day1;

public class LearnDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// datatype variable = value;
		int age = 45;
		float ht = 5.4f;
		double salary = 34.87676896969;
		char initial = 'R';
		String name = "Mugesh123$";
		long phno = 87876564359679l;
		boolean isCar = false;
		// to print the variable ==> use syso without double quotes with varName
		System.out.println("My age is"+age); 
		// + --> Concatenation
		System.out.println(ht);
		System.out.println(salary);
		System.out.println(initial+"."+name);
		System.out.println(isCar);
		System.out.println(phno);
		
	}
} 